import React, {useState} from 'react'
import {FaShoppingCart} from "react-icons/fa"
import Order from './Order'
const showOrders=(props)=>{
  let summa=0;
  props.orders.forEach(el=>summa += Number.parseFloat(el.price).toFixed(2))
  return (<>{props.orders.map(el => {return (
    <Order onDelete={props.onDelete} key={el.id} item={el}/>
  )})}
  <p className='summa'>Сумма: {summa}$</p>
  </>)
}
const showNothing = ()=>{
  return (<div className='empty'>
    <h2>Товаров нет</h2>
  </div>)
}
export default function Header(props) {
  let [cartOpen, setCartOpen]=useState(false)
  return (
    <header>
        <div>
            <span className='logo'>House Staff</span>
            <ul className="nav">
                <li>Про нас</li>
                <li>Контакты</li>
                <li>Кабинет</li>
            </ul>
            <FaShoppingCart className={`shop-cart-button ${cartOpen && 'active'}`} onClick={()=>setCartOpen(cartOpen = !cartOpen)}/>
            {cartOpen && (
              <div className="shop-cart">
                {props.orders.length>0 ? 
                showOrders(props) : showNothing()}
                
              </div>
            )} 
        </div>
        <div className='presentation'></div>
    </header>
  )
}
